    public function verInfoRelacion($id){
        if($id==0){
            $LcResp = $this->verMapaGeneral();
            return $LcResp;
        };

        $info = Relacion::where('idDS', $id)->get();
       

        $jsonMapa = $info[0]->jsonDatosMapa;
        //$jsonMapa = json_decode($jsonMapa);
        $mapasBase = relMapaB::where('idDS', $id)
        ->leftJoin('cat_mapas_base as T2', 'T2.idMapa', 'ds_rel_mapabase.idMapa')
        ->get();

        if($mapasBase == '[]'){
            $mapasBase = MapaBase::where('idMapa', '<', 4)->get();
        }

       
        
        $infoMapa = $this->traerInfoMapa($jsonMapa, $mapasBase);
        
        $capas = Capa::where('idDocumentSection', $id)
        ->where('cEstatus', 'A')->where('iPublic', '1')
        ->orderBy('iOrden')->get();
        
        $oCapas = $this->traeCapas($capas, $id);
       
        $oCapas = '{'.$oCapas.'}';

        $oInfo = '{ "descripcion": "DESCRIPCIÓN DE LA RELACIÓN", "fuentes": { "0": { "cita": "1986 Acuña, Rene. Relaciones geográficas de la nueva españa.", "url": "http://www.google.com"} } }';
        
       $LcResp = '{ "infoMapa": '.$infoMapa.', "capas": '.$oCapas.', "infoRelacion": '.$oInfo.' }';

       

       return $LcResp;

        return response()->json([
            'infoMapa' => json_decode($infoMapa),
            'capas' => json_decode($oCapas),
        ]);

    }

    private function traeCapas($capas, $idRelacion){
        $cCapas = "";
        $i = 0;
        if($capas == "[]"){
            $pet = 'select T2.*, IFNULL( T4.cTipo, "Sin definir") as cTipoLugar, 
            IFNULL( T4.ctipoSimbolo, "circulo") as cTipoSimbolo, 
            IFNULL( T4.nDiametro, 5) as nDiametro, 
            IFNULL( T4.cColor, "white") as cColor, T4.cURL, T3.cTipo as cTipoElemento from
            rel_lugar_document_section as T1 
            left join lugares as T2 on T1.idLugar = T2.id
            left join cat_tipoelemento as T3 on T3.idTipo = T2.idTipoElemento
            left join cat_tipolugar as T4 on T4.id = T2.idTipo
            where T1.idDS = '.$idRelacion.' and T2.cEstatus = "A"';
           
            
        
            $capa = DB::select($pet);
           
            $cCapas = $cCapas.'"'.$i++.'": {'.
                '"nombre": '.'"Topónimos", "elementos": {';
            $elementos = $this->ordenaElementos($capa, $idRelacion);
            
            $cCapas = $cCapas.$elementos.'}},';

        } else {
            foreach($capas as $capa){
                $cCapas = $cCapas.'"'.$i++.'": {'.
                    '"nombre": '.'"'.$capa['cNombre'].'", "elementos": {';
                $elementos = $this->traeElementos($capa, $idRelacion);
                
                $cCapas = $cCapas.$elementos.'}},';
            }
            
        }
        $cCapas = substr($cCapas, 0, -1);
        return $cCapas;
    }

