    private function traeElementos($capa, $idRel){
        $lugares = lugar::select([
            'lugares.*',
            DB::raw('IFNULL( T3.cTipo, "Sin definir") as cTipoLugar'),
            DB::raw('IFNULL( T3.ctipoSimbolo, "circulo") as cTipoSimbolo'),
            DB::raw('IFNULL( T3.nDiametro, 5) as nDiametro'),
            DB::raw('IFNULL( T3.cColor, "white") as cColor'),
            'T3.cURL', 'T7.cTipo as cTipoElemento'
        ])
        ->leftJoin('capa_rel_lugar as T2', 'T2.idLugar', 'lugares.id')
        ->leftJoin('cat_tipoelemento as T7', 'T7.idTipo', 'lugares.idTipoElemento')
        ->leftJoin('cat_tipolugar as T3', 'T3.id', 'lugares.idTipo')
        ->where('T2.idCapa', $capa['idCapa'])->get();
        
        $i = 0;
        $cLugares = "";
        foreach($lugares as $lugar){
            $cLugares = $cLugares.' "'.$i++.'": { '.
                '"id": '.'"'.$lugar['id'].'",'.
                '"tipo": '.'"'.$lugar['cTipoElemento'].'",'.
                '"nombre": '.'"'.$lugar['Placename'].'",'.
                '"simbolo": { '.
                    '"tipo": '.'"'.$lugar['cTipoSimbolo'].'",'.
                    '"color": '.'"'.$lugar['cColor'].'",'.
                    '"diametro": '.''.$lugar['nDiametro'].','.
                    '"url": '.'"'.$lugar['cURL'].'" },';
            if($lugar->idTipoElemento == 1){
                $cLugares = $cLugares.'"coordenadas": { '.
                    '"lat": '.'"'.$lugar['X'].'",'.
                    '"long": '.'"'.$lugar['Y'].'"';
                    $cLugares = $cLugares.'},';
            } else if($lugar->idTipoElemento == 5) {
                $cLugares = $cLugares.'"coordenadas": '.''.$lugar['X'].',';
            } else {
                $cLugares = $cLugares.'"coordenadas": '.'"'.$lugar['X'].'",';
            }
            
            $popup = '<center><b>'.$lugar['Placename'].'</b><center><br><b>Coordenadas</b><br><b>Latitud:</b>'.$lugar['X'].'<br><b>Longitud:</b>'.$lugar['Y'];
            $cLugares = $cLugares.'"popup": "'.$popup.'"},';
        }

        $cLugares = substr($cLugares, 0, -1);
        return $cLugares;
    }

    public function ordenaElementos($lugares){
        $i = 0;
        $cLugares = "";
       
        foreach($lugares as $lugar){
            
            $cLugares = $cLugares.' "'.$i++.'": { '.
                '"id": '.'"'.$lugar->id.'",'.
                '"tipo": '.'"'.$lugar->cTipoElemento.'",'.
                '"nombre": '.'"'.$lugar->Placename.'",'.
                '"simbolo": { '.
                    '"tipo": '.'"'.$lugar->cTipoSimbolo.'",'.
                    '"color": '.'"'.$lugar->cColor.'",'.
                    '"diametro": '.''.$lugar->nDiametro.','.
                    '"url": '.'"'.$lugar->cURL.'" },';
            if($lugar->idTipoElemento == 1){
                $cLugares = $cLugares.'"coordenadas": { '.
                    '"lat": '.'"'.$lugar->X.'",'.
                    '"long": '.'"'.$lugar->Y.'"';
                    $cLugares = $cLugares.'},';
            } else if($lugar->idTipoElemento == 5) {
                $cLugares = $cLugares.'"coordenadas": '.''.$lugar->X.',';
            } else {
                $cLugares = $cLugares.'"coordenadas": '.'"'.$lugar->X.'",';
            }
            
            $popup = '<center><h6><b>'.$lugar->Placename.'</b></h6></center><b>Coordenadas</b><br><b>Latitud:</b>'.$lugar->X.'<br><b>Longitud:</b>'.$lugar->Y.'<br><b>Nombre Moderno:</b>'.$lugar->ModernName;
            $cLugares = $cLugares.'"popup": "'.$popup.'"},';
        }

        $cLugares = substr($cLugares, 0, -1);
        return $cLugares;
    }

    private function traerInfoMapa($jsonMapa, $mapasBase){
        $jsonMapa = substr($jsonMapa, 0, -1);
        $iMapa = 0;
        $cMapas = '"mapasBase": {';
        foreach($mapasBase as $mapa){
            $cMapas = $cMapas.' "'.$iMapa++.'": {'.
                '"nombre": '.'"'.$mapa['cNombre'].'",'.
                '"atribution": '.'"'.$mapa['cAtribucion'].'",'.
                '"link": '.'"'.$mapa['cURL'].'",';
            if($mapa['cNombre'] == 'INEGI'){
                $cMapas = $cMapas.'"visible": true },';
            } else {
                $cMapas = $cMapas.'"visible": false },';
            }
        }
        $cMapas = substr($cMapas, 0, -1);
        $cMapas = $cMapas."}";
        $jsonMapa = $jsonMapa.','.$cMapas.'}';

        return $jsonMapa;
    }








